import React, { Component } from 'react'
import './ChatApp.scss';

// import components
import ChatroomList from '../ChatroomList';
import ChatWindow from '../ChatWindow';

// import backend services
import ChatroomAPI from '../../api/ChatroomAPI';

class ChatApp extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.handleRoomSelect = this.handleRoomSelect.bind(this);
    }

    componentDidMount() {
        ChatroomAPI.getAvailableChatrooms().then(response => {
            this.setState({
                availableChatrooms: response.roomIds
            });
        });
    }

    handleRoomSelect(event) {
        this.setState({
            activeChatWindow: event.target.value
        });
    }

    createNewChatroom() {
        ChatroomAPI.createChatroom().then(response => {
            console.log(response);
        });
    }

    render() {
        const chatWindow = this.state.activeChatWindow ? <ChatWindow roomId={this.state.activeChatWindow} /> : <></>;
        console.log(this.state.availableChatrooms);
        return (
            <div className="ChatApp">
                <button onClick={this.createNewChatroom}>Create New Chatroom</button>
                <ChatroomList 
                    handleRoomSelect={this.handleRoomSelect}
                    chatrooms={this.state.availableChatrooms ? this.state.availableChatrooms : []} 
                />
                {chatWindow}
            </div>
        )
    }
}

export default ChatApp;
