import React, { Component } from 'react';
import "./App.css";

import Header from './components/Header';
import ChatHistory from './components/ChatHistory';
import ChatInput from './components/ChatInput'
import ChatRoomConnection from './api/index';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeChatRooms: []
    };
  }

  componentDidMount() {
    this.getAvailableChatRooms().then(data => {
      console.log(data);
      /*this.setState(prevState => {
        this.getAvailableChatRooms = []
      })*/
    })
  }

  getAvailableChatRooms = async () => {
    const response = await fetch("http://localhost:8080/chatroom", {
      method: "GET",
      mode: "cors"
    });
    return response.json();
  }

  connectToRoom = () => {
    this.chatRoomConnection = new ChatRoomConnection(this.state.nextRoomId, (msg) => {
      console.log("New Message");
      this.setState(prevState => ({
        chatHistory: [...prevState.chatHistory, msg],
        nextRoomId: prevState.nextRoomId + 1
      }))
    });
  }

  createChatroom = async () => {
    const response = await fetch("http://localhost:8080/chatroom", {
      method: "POST",
      mode: "cors"
    });
    return response.json();
  }

  send = (event) => {
    if (event.keyCode === 13) {
      this.chatRoomConnection.sendMessage(event.target.value);
      event.target.value = "";
    }
  }

  render() {
    return (
      <div className="App">
        <Header newChatroomAction={this.createChatroom} />
        <ChatHistory chatHistory={this.state.chatHistory}/>
        <ChatInput send={this.send} />
      </div>
    );
  }
}

export default App;
