import React, { Component } from 'react';
import './ChatWindow.scss';

// import backend services
import ChatroomConnection from '../../api/ChatroomConnection';

// import components
import ChatMessage from '../ChatMessage';
import ChatInput from '../ChatInput';

export class ChatWindow extends Component {
    constructor(props) {
        super(props);
        this.state = {
            chatHistory: []
        }
        this.connection = new ChatroomConnection(this.ID, msg => {
            console.log("New Message");
            this.setState(prevState => ({
                chatHistory: [...prevState.chatHistory, msg]
            }));
        });
    }

    send = event => {
        if (event.keyCode === 13) {
            this.chatRoomConnection.sendMessage(event.target.value);
            event.target.value = "";
        }
    }

    render() {
        const messages = this.state.chatHistory.map((val, index) => (
            <ChatMessage>{val}</ChatMessage>
        ));
        return (
            <div className="ChatWindow">
                {this.props.roomId}
            </div>
        )
    }
}

export default ChatWindow;
