import React, { Component } from 'react'
import './ChatApp.scss';

// import components
import ChatroomList from '../ChatroomList';
import ChatWindow from '../ChatWindow';

// import backend services
import ChatroomAPI from '../../api/ChatroomAPI';

class ChatApp extends Component {

    componentDidMount() {
        this.setState({
            availableChatrooms: ChatroomAPI.getAvailableChatrooms()
        });
    }

    handleRoomSelection(val) {
        this.setState({
            activeChatWindow: val
        });
    }

    render() {
        const activeChatWindow = this.state.activeChatWindow ? <ChatWindow roomId={this.state.activeChatWindow} /> : <></>;
        return (
            <div className="ChatApp">
                <ChatroomList chatrooms={this.state.availableChatrooms} />
                {activeChatWindow}
            </div>
        )
    }
}

export default ChatApp;
