import React, { Component } from 'react';
import "./App.css";

import Header from './components/Header';
import ChatApp from './components/ChatApp';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeChatRooms: [],
      availableChatrooms: []
    };
  }

  componentDidMount() {
    this._getAvailableChatRooms().then(data => {
      console.log(data);
      this.setState(prevState => {
        availableChatrooms = data.roomIds;
      });
    });
  }

  connectToRoom = () => {
    this.chatRoomConnection = new ChatroomConnection(this.state.nextRoomId, (msg) => {
      console.log("New Message");
      this.setState(prevState => ({
        chatHistory: [...prevState.chatHistory, msg],
        nextRoomId: prevState.nextRoomId + 1
      }))
    });
  }

  createNewChatroom = () => {
    this._createChatroom().then(data => {
      console.log(data);
      this.setState(prevState => {
        activeChatRooms: [...prevState.activeChatRooms, data]
      });
    });
  }



  send = (event) => {
    if (event.keyCode === 13) {
      this.chatRoomConnection.sendMessage(event.target.value);
      event.target.value = "";
    }
  }

  render() {
    return (
      <ChatApp />
    );
  }
}

export default App;
